import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import Content from "../../components/Content";
import axios from "axios";

const PsicologoEdit = () => {
  const { id } = useParams();
  const [nomPsicologo, setNomPsicologo] = useState("");
  const [ap1Psicologo, setAp1Psicologo] = useState("");
  const [ap2Psicologo, setAp2Psicologo] = useState("");
  const [edadPsicologo, setEdadPsicologo] = useState("");
  const [sexoPsicologo, setSexoPsicologo] = useState("");
  const [especialidad, setEspecialidad] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(`http://localhost:4002/psicologos/${id}`);
        const data = response.data;
        setNomPsicologo(data.nomPsicolgo);
        setAp1Psicologo(data.ap1Psicolgo);
        setAp2Psicologo(data.ap2Psicolgo);
        setEdadPsicologo(data.edadPsicolgo);
        setSexoPsicologo(data.sexoPsicolgo);
        setEspecialidad(data.especialidad);
      } catch (error) {
        console.error("Error al obtener el psic贸logo:", error);
      }
    };

    fetchData();
  }, [id]);

  const handleNomPsicolgo = (event) => {
    setNomPsicologo(event.target.value);
  };

  const handleAp1Psicolgo = (event) => {
    setAp1Psicologo(event.target.value);
  };

  const handleAp2Psicolgo = (event) => {
    setAp2Psicologo(event.target.value);
  };

  const handleEdadPsicolgo = (event) => {
    setEdadPsicologo(event.target.value);
  };

  const handleSexoPsicolgo = (event) => {
    setSexoPsicologo(event.target.value);
  };

  const handleEspecialidad = (event) => {
    setEspecialidad(event.target.value);
  };

  const handleSubmit = async () => {
    const psicologo = {
      nomPsicologo,
      ap1Psicologo,
      ap2Psicologo,
      edadPsicologo,
      sexoPsicologo,
      especialidad,
    };

    try {
      await axios.put(`http://localhost:4002/psicologos/${id}`, psicologo);
      navigate("/psicologos");
    } catch (error) {
      console.error("Error al actualizar el psic贸logo:", error);
    }
  };

  return (
    <Content>
      <h1>EDITAR PSICOLOGO</h1>
      <label>
        Nombre:
        <input
          type="text"
          value={nomPsicologo}
          onChange={handleNomPsicolgo}
        />
      </label>
      <label>
        Ap1:
        <input
          type="text"
          value={ap1Psicologo}
          onChange={handleAp1Psicolgo}
        />
      </label>
      <label>
        Ap2:
        <input
          type="text"
          value={ap2Psicologo}
          onChange={handleAp2Psicolgo}
        />
      </label>
      <label>
        Edad:
        <input
          type="text"
          value={edadPsicologo}
          onChange={handleEdadPsicolgo}
        />
      </label>
      <label>
        Sexo:
        <input
          type="text"
          value={sexoPsicologo}
          onChange={handleSexoPsicolgo}
        />
      </label>
      <label>
        Especialidad:
        <input
          type="text"
          value={especialidad}
          onChange={handleEspecialidad}
        />
      </label>
      <button onClick={handleSubmit}>Actualizar</button>
    </Content>
  );
};

export default PsicologoEdit;
