import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
// import { useNavigate } from "react-router-dom";
import { useNavigate } from "react-router-dom";
import Content from "../../components/Content";
import axios from "axios";
import PacienteService from "../../services/PacienteServices";
import PsicologoServices from "../../services/PsicologoServices";
import RadioGroup from "@mui/material/RadioGroup";
import FormControlLabel from "@mui/material/FormControlLabel";
import FormLabel from "@mui/material/FormLabel";
import Radio from "@mui/material/Radio";
import Swal from 'sweetalert2';
import Layout from "../../containers/Layout";

// LIBRERIAS FORMULARIOS
import {
  FormControl,
  InputLabel,
  TextField,
  MenuItem,
  Select,
  Button,
} from "@mui/material";

const PacientesEdit = (id) => {
  const [nomPaciente, setnomPaciente] = useState("");
  const [ap1Paciente, setap1Paciente] = useState("");
  const [ap2Paciente, setap2Paciente] = useState("");
  const [edadPaciente, setedadPaciente] = useState("");
  const [sexoPaciente, setsexoPaciente] = useState("");
  const [psicologo, setPsicologo] = useState("");
  const [psicologos, setPsicologos] = useState([]);

  const routeParams = useParams();
  const navigate = useNavigate();
  const server = `http://localhost:4002`;

  function mostrarError(mensaje) {
    Swal.fire({
        title: '¡Error!',
        text: mensaje,
        icon: 'error',
        confirmButtonText: 'Aceptar'
    });
  }

  const getData = async () => {
    const data = await axios.get(`${server}/pacientes?filter=` + JSON.stringify({
      "where": {
        "idPsicologo": routeParams.id
      },
      "include": [
        {
          "relation": "pacientes_usuarios"
        },
      ]
    }));
    console.log("-- usu --",data);

    setnomPaciente(data.nomPaciente);
    setap1Paciente(data.ap1Paciente);
    setap2Paciente(data.ap2Paciente);
    setedadPaciente(data.edadPaciente);
    setsexoPaciente(data.sexoPaciente);

    const data_p = await PsicologoServices.getPsicologos();
    console.log(data_p);
    setPsicologos(data_p);
  };

  useEffect(() => {
    getData();
  }, []);

  const handlePaciente = (event) => {
    setnomPaciente(event.target.value);
  };
  const handleap1Paciente = (event) => {
    setap1Paciente(event.target.value);
  };
  const handleap2Paciente = (event) => {
    setap2Paciente(event.target.value);
  };
  const handleedadPaciente = (event) => {
    setedadPaciente(event.target.value);
  };
  const handlesexoPaciente = (event) => {
    setsexoPaciente(event.target.value);
  };
  const handlePsicologo = (event) => {
    setPsicologo(event.target.value);
  };
  const handlePsicologos = (event) => {
    setPsicologos(event.target.value);
  };
  const Cancelar = () => {
    navigate("/pacientes");
  };

  const handleSubmit = () => {
    console.log(psicologo);
    if(psicologo===""){
      mostrarError("Falta agregar psicologo");
  }
  else{
    let paciente = {
      nomPaciente: nomPaciente,
      ap1Paciente: ap1Paciente,
      ap2Paciente: ap2Paciente,
      edadPaciente: edadPaciente,
      sexoPaciente: sexoPaciente,
      idPsicologo: parseInt(psicologo),
    };

  PacienteService.updatePaciente(paciente, routeParams.id);
  navigate("/pacientes");
  }
}

  return (
    <Layout>
        <Content>
        <h1>EDITAR PACIENTE</h1>
        <div className="div-formulario">
          <div>
            <InputLabel id="psicologo">
              <h3>Psicologo</h3>
            </InputLabel>
            <select
              labelId="psicologo"
              id="psicologo"
              name="psicologo"
              onChange={handlePsicologo}
            >
              <option disabled selected>
                Selecciona un psicologo
              </option>
              {psicologos.map((psicologo) => (
                <option value={psicologo.idPsicologo}>
                  {psicologo.nomPsicologo} {psicologo.ap1Psicologo}{" "}
                  {psicologo.ap2Psicologo}
                </option>
              ))}
            </select>
          </div>
          <div>
            <label class="textoCaja"> Paciente: </label>
            <input
              type="text"
              class="redondeado"
              name="nomPaciente"
              value={nomPaciente}
              onChange={handlePaciente}
            />
          </div>
          <div>
            <label class="textoCaja">Ap1:</label>
            <input
              type="text"
              class="redondeado"
              name="ap1Paciente"
              value={ap1Paciente}
              onChange={handleap1Paciente}
            />
          </div>
          <div>
            <label class="textoCaja">Ap2:</label>
            <input
              type="text"
              class="redondeado"
              name="ap2Paciente"
              value={ap2Paciente}
              onChange={handleap2Paciente}
            />
          </div>
          <div>
            <label class="textoCaja">Edad:</label>
            <input
              type="text"
              class="redondeado"
              name="edadPaciente"
              value={edadPaciente}
              onChange={handleedadPaciente}
            />
          </div>
          <div>
          <FormLabel
              id="demo-row-radio-buttons-group-label"
              style={{
                fontWeight: "bold",
              }}
            >
              Sexo:
            </FormLabel>
            <RadioGroup
              row
              aria-labelledby="demo-radio-buttons-group-label"
              onChange={handlesexoPaciente}
              name="radio-buttons-group"
              value={sexoPaciente}
            >
              <FormControlLabel
                value="M"
                className="lblRadio"
                control={
                  <Radio
                  />
                }
                label="Masculino"
              />
              <FormControlLabel
                value="F"
                className="lblRadio"
                control={
                  <Radio
                  />
                }
                label="Femenino"
              />
            </RadioGroup>
          </div>
          <div>
            <button class="estiloBoton" onClick={handleSubmit}>
              Actualizar
            </button>
          </div>
          <div>
            <button class="estiloBoton" onClick={Cancelar}>
              {" "}
              Cancelar{" "}
            </button>
          </div>
        </div>
      </Content>
    </Layout>
    
  );
};
export default PacientesEdit;
