import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { useNavigate } from "react-router-dom";
import Content from "../../components/Content";
import axios from "axios";
import AgendaServices from "../../services/AgendaServices";

const AgendaEdit = (id) => {
  const [comentario, setComentario] = useState("");
  const [fecha, setFecha] = useState("");
  const [hora, setHora] = useState("");
  
  const routeParams = useParams();
  const navigate = useNavigate();
  const server = `http://localhost:4002`;

  const getData = async () => {
    const { data } = await axios.get(`${server}/agenda/${routeParams.id}`);
    setComentario(data.comentario);
    setFecha(data.fecha);
    setHora(data.hora);
  };

  useEffect(() => {
    getData();
  }, []);
  
  const handleComentario = (event) => {
    setComentario(event.target.value);
  };
  const handleFecha = (event) => {
    setFecha(event.target.value);
  };
  const handleHora = (event) => {
    setHora(event.target.value);
  };
  
  const handleSubmit = () => {
    // const server = `http://localhost:4000`;
    let agenda = {
      comentario: comentario,
      fecha: fecha,
      hora: hora,
    };
    AgendaServices.updateAgenda(agenda, routeParams.id);
    navigate("/agenda");
  };
  return (
    <Content>
      <h1>EDITAR PACIENTE</h1>
      <label>
        Paciente:
        <input
          type="text"
          name="nomPaciente"
          value={nomPaciente}
          onChange={handlePaciente}
        />
      </label>

      <label>
        Ap1:
        <input
          type="text"
          name="ap1Paciente"
          value={ap1Paciente}
          onChange={handleap1Paciente}
        />
      </label>

      <label>
        Ap2:
        <input
          type="text"
          name="ap2Paciente"
          value={ap2Paciente}
          onChange={handleap2Paciente}
        />
      </label>

      <label>
        Edad:
        <input
          type="text"
          name="edadPaciente"
          value={edadPaciente}
          onChange={handleedadPaciente}
        />
      </label>

      <label>
        Sexo:
        <input
          type="text"
          name="sexoPaciente"
          value={sexoPaciente}
          onChange={handlesexoPaciente}
        />
      </label>

      <button onClick={handleSubmit}>Actualizar</button>
    </Content>
  );
};
export default AgendaEdit;
