import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { useNavigate } from "react-router-dom";
import Content from "../../components/Content";
import axios from "axios";
import AgendaServices from "../../services/AgendaServices";

const AgendaEdit = (id) => {
  const [comentario, setComentario] = useState("");
  const [fecha, setFecha] = useState("");
  const [hora, setHora] = useState("");
  const [idPaciente, setidPaciente] = useState("");
  const [nomPaciente, setnomPaciente] = useState("");
  const [ap1Paciente, setap1Paciente] = useState("");
  const [ap2Paciente, setap2Paciente] = useState("");
  
  const routeParams = useParams();
  const navigate = useNavigate();
  const server = `http://localhost:4002`;

  const getData = async () => {
    const {data}=await axios.get(`${server}/agenda/${routeParams.id}?filter=` + JSON.stringify({
      "include": [
        {
          "relation": "agenda_pacientes"
        },
      ]
    }));
    setComentario(data.comentario);
    setFecha(data.fecha);
    setHora(data.hora);
    setnomPaciente(data.nomPaciente);
    setap1Paciente(data.ap1Paciente);
    setap2Paciente(data.ap2Paciente);
  };

  useEffect(() => {
    getData();
  }, []);
  
  const handleComentario = (event) => {
    setComentario(event.target.value);
  };
  const handleFecha = (event) => {
    setFecha(event.target.value);
  };
  const handleHora = (event) => {
    setHora(event.target.value);
  };

  const handleSubmit = () => {
    let agenda = {
      comentario: comentario,
      fecha: fecha,
      hora: hora,
      idPaciente: idPaciente,
    };
    AgendaServices.updateAgenda(agenda, routeParams.id);
    navigate("/agenda");
  };
  
  return (
    <Content>
      <h1>EDITAR AGENDA</h1>
      <label>
        Nombre del paciente:
      </label>
      <input type="text" value={nomPaciente+" "+ap1Paciente+" "+ap2Paciente} readOnly/>
      <label>
        Comentario:
        <input
          type="text"
          name="comentario"
          value={comentario}
          onChange={handleComentario}
        />
      </label>

      <label>
        Fecha:
        <input
          type="text"
          name="fecha"
          value={fecha}
          onChange={handleFecha}
        />
      </label>

      <label>
        Hora:
        <input
          type="text"
          name="hora"
          value={hora}
          onChange={handleHora}
        />
      </label>

      <button onClick={handleSubmit}>Actualizar</button>
    </Content>
  );
};
export default AgendaEdit;
