import React, { useState } from "react";
import Content from "../../components/Content";
import axios from "axios";

import {
  FormControl,
  FormLabel,
  InputLabel,
  RadioGroup,
  Radio,
  FormControlLabel,
  TextField,
  MenuItem,
  Select,
  FormGroup,
  Checkbox,
  Button,
} from "@mui/material";

import { Grid } from "@mui/material";
import PersonAddIcon from "@mui/icons-material/PersonAdd";

const Login = () => {
  const [correo, setCorreo] = useState("");
  const [contrasena, setContrasena] = useState("");

  // variable handleLogin para capturar texto del campo
  const handleCorreo = (event) => {
    setCorreo(event.target.value);
  };
  const handleContrasena = (event) => {
    setContrasena(event.target.value);
  };
  const handleSubmit = () => {
    let login = {
      correo: correo,
      contrasena: contrasena,
    };
  };

  return (
    <Content>
      <h1> LOG IN </h1>
      <label>
        Correo:
        <input type="text" name="correo" onChange={handleCorreo} />   {/*handleCorreo obtiene el texto del campo */}
      </label>
      <label>
        Edad:
        <input type="text" name="edad" onChange={handleContrasena} />
      </label>
      <button onClick={handleSubmit}>Add</button>
    </Content>
  );
};

export default Login;
