import React, { useState } from "react";
import Content from "../components/Content";
import { useNavigate } from "react-router-dom";
import Axios from "axios";
import "../styles/Login.css";

import {
  FormControl,
  FormLabel,
  InputLabel,
  RadioGroup,
  Radio,
  FormControlLabel,
  TextField,
  MenuItem,
  Select,
  FormGroup,
  Checkbox,
  Button,
} from "@mui/material";

import { Grid } from "@mui/material";
import PersonAddIcon from "@mui/icons-material/PersonAdd";

const Login = () => {
  const [correo, setCorreo] = useState("");
  const [contrasena, setContrasena] = useState("");
  const navigate = useNavigate();

  const handleCorreo = (event) => {
    setCorreo(event.target.value);
  };
  const handleContrasena = (event) => {
    setContrasena(event.target.value);
  };
  const handleSubmit = (e) => {
    e.preventDefault();

    Axios.get(`http://[::1]:4002/logins`)
      .then(response => {
        const usuarios = response.data;
        const usuario = usuarios.find(u => u.correo === correo && u.contrasena === contrasena);
        if (correo==='cmursal@iez.com') {
          navigate("/pacientes");
        } else {
          if (correo==='scampos@iez.com') {
            navigate("/psicologos");
          } else {
            if (correo==='lucero@gmail.com') {
              navigate("/calendario");
            } else {
              alert("Correo o contraseña inválida. Por favor, inténtelo de nuevo.");
            }
          }
        }})
        .catch(error => {
          console.error('Error al autenticar al usuario:', error);
          });
    }

  return (
    <div class="wrapper">
      <form action="">
        <h1> Login</h1>
        <div class="input-box">
          <input type="text" name="correo" onChange={handleCorreo} placeholder="Correo" required/>
        </div>
        <div class="input-box">
          <input type="password" name="contrasena" onChange={handleContrasena} placeholder="Contrasena" required/>
        </div>
        <div class="remember-forgot">
          <label>
            <input type="checkbox"> Recordar contrasena </input>
          </label>
          <a href="#"> Olvidaste la contrasena?</a>
        </div>
        <button type="submit" class="btn" onClick={handleSubmit} > Ingresar </button>
        <div class="register-link">
          <p> No tengo una cuenta <a href="#"> Registrar </a> </p>
        </div>
      </form>
    </div>
  );
};

export default Login;
