import React, { useState } from "react";
import Content from "../../components/Content";
import axios from "axios";

import {
  FormControl,
  FormLabel,
  InputLabel,
  RadioGroup,
  Radio,
  FormControlLabel,
  TextField,
  MenuItem,
  Select,
  FormGroup,
  Checkbox,
  Button,
} from "@mui/material";

import { Grid } from "@mui/material";
import PersonAddIcon from "@mui/icons-material/PersonAdd";

const PacientesAdd = () => {
  const [nombre, setNombre] = useState("");
  const [edad, setEdad] = useState("");

  // variable handleAlumno para capturar texto del campo
  const handlePaciente = (event) => {
    setNombre(event.target.value);
  };
  const handleEdad = (event) => {
    setEdad(event.target.value);
  };
  const handleSubmit = () => {
    let paciente = {
      nombre: nombre,
      edad: edad,
    };
  };

  return (
    <Content>
      <h1> CAPTURA DE PACIENTES </h1>
      <label>
        Nombre del paciente:
        <input type="text" name="nombre" onChange={handlePaciente} />   {/*handleAlumno obtiene el texto del campo */}
      </label>
      <label>
        Edad:
        <input type="text" name="edad" onChange={handleEdad} />
      </label>
      <button onClick={handleSubmit}>Add</button>
    </Content>
  );
};

export default PacientesAdd;
