import React, { useState } from "react";
import Content from "../../components/Content";
import Button from "@mui/material/Button";
import HowToRegIcon from '@mui/icons-material/HowToReg';
import LoginIcon from '@mui/icons-material/Login';
import { useNavigate } from "react-router-dom";
import Axios from 'axios';

const Inicio = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  }

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  }

  const handleSubmit = (e) => {
    e.preventDefault();

    Axios.get(`http://[::1]:4002/usuarios`)
      .then(response => {
        const usuarios = response.data;
        const usuario = usuarios.find(u => u.correo === email && u.contrasena === password);
        if (usuario) {
          navigate("/");
        } else {
          alert("Correo o constraseña inválidas. Por favor, inténtelo de nuevo.");
        }
      })
      .catch(error => {
        console.error('Error al autenticar al usuario:', error);
      });
  }

  return (
    <Content>
      <div className="login-container">
        <div className="login-form">
          <h1>PSIQUEDATA App</h1>
          <h2>¡Bienvenido!</h2>
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label htmlFor="email">Correo:</label>
              <input
                type="email"
                id="email"
                value={email}
                onChange={handleEmailChange}
              />
            </div>
            <div className="form-group">
              <label htmlFor="password">Contraseña:</label>
              <input
                type="password"
                id="password"
                value={password}
                onChange={handlePasswordChange}
              />
            </div>
            <div className="forgot-password">
            <a href="/olvidar-contrasena" style={{ fontSize: '12px', color: 'red' }}>
              ¿Olvidaste tu contraseña?
            </a>
          </div>
            <br></br>
            <Button type="submit" variant="contained" color="success" startIcon={<LoginIcon />}>
              Iniciar Sesión
            </Button>
            <span style={{ marginRight: '10px' }}></span>
            <a href="/psicologos">
              <Button variant="contained" color="success" startIcon={<HowToRegIcon />}>
                Registrarse
              </Button>
            </a>
          </form>
        </div>
      </div>
    </Content>
  );
};

export default Inicio;
