import React, { useState } from "react";
import Content from "../components/Content";
import { useNavigate } from "react-router-dom";
import Axios from "axios";

import {
  FormControl,
  FormLabel,
  InputLabel,
  RadioGroup,
  Radio,
  FormControlLabel,
  TextField,
  MenuItem,
  Select,
  FormGroup,
  Checkbox,
  Button,
} from "@mui/material";

import { Grid } from "@mui/material";
import PersonAddIcon from "@mui/icons-material/PersonAdd";

const Login = () => {
  const [correo, setCorreo] = useState("");
  const [contrasena, setContrasena] = useState("");
  const navigate = useNavigate();

  const handleCorreo = (event) => {
    setCorreo(event.target.value);
  };
  const handleContrasena = (event) => {
    setContrasena(event.target.value);
  };
  const handleSubmit = (e) => {
    e.preventDefault();

    Axios.get(`http://[::1]:4002/usuarios`)
      .then(response => {
        const usuarios = response.data;
        const usuario = usuarios.find(u => u.correo === correo && u.contrasena === contrasena);
        if (usuario) {
          navigate("/psicologos");
        } else {
          alert("Correo o contraseña inválida. Por favor, inténtelo de nuevo.");
        }
      })
      .catch(error => {
        console.error('Error al autenticar al usuario:', error);
      });
  }

  return (
    <Content>
      <p> 
        <h1> LOGIN </h1> 
      </p>
      <p>
        <label>
          Correo:
          <input type="text" name="correo" onChange={handleCorreo} />   {/*handleCorreo obtiene el texto del campo */}
        </label>
      </p>
      <p>
        <label>
          Contrasena:
          <input type="text" name="contrasena" onChange={handleContrasena} />
        </label>
      </p>
      <p>
        <button onClick={handleSubmit}> 
          Entrar 
        </button>
        &nbsp;
        <button onClick={handleSubmit}> 
          Salir 
        </button>
      </p>
      
    </Content>
  );
};

export default Login;
