import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { useNavigate } from "react-router-dom";
import Content from "../../components/Content";
import axios from "axios";
import ExpedienteServices from "../../services/ExpedienteServices";


const ExpedienteEdit = (id) => {
  const [paciente, setPaciente] = useState("");
  const [familiograma, setFamiliograma] = useState("");
  const [antecedentes, setAntecedentes] = useState("");
  const [padecimientos, setPadecimientos] = useState("");
  const [tratamientos, setTratamientos] = useState("");
  const [diagnostico, setDiagnostico] = useState("");

  const routeParams = useParams();
  const navigate = useNavigate();
  const server = `http://localhost:4002`;

  const getData = async () => {
    const { data } = await axios.get(`${server}/expedientes/${routeParams.id}`);
    setPaciente(data.paciente);
    setFamiliograma(data.familiograma);
    setAntecedentes(data.antecedentes);
    setPadecimientos(data.padecimientos);
    setTratamientos(data.tratamientos);
    setDiagnostico(data.diagnostico);
  };

  useEffect(() => {
    getData();
  }, []);
  
  const handlePaciente = (event) => {
    setPaciente(event.target.value);
  };
  const handleFamiliograma = (event) => {
    setFamiliograma(event.target.value);
  };
  const handleAntecedentes = (event) => {
    setAntecedentes(event.target.value);
  };
  const handlePadecimientos = (event) => {
    setPadecimientos(event.target.value);
  };
  const handleTratamientos = (event) => {
    setTratamientos(event.target.value);
  };
  const handleDiagnostico = (event) => {
    setDiagnostico(event.target.value);
  };
  
  const handleSubmit = () => {
    let expediente = {
      paciente: paciente,
      familiograma: familiograma,
      antecedentes: antecedentes,
      padecimientos: padecimientos,
      tratamientos: tratamientos,
      diagnostico: diagnostico,
    };
    ExpedienteServices.updateExpediente(expediente, routeParams.id);
    navigate("/expedientes");
  };

  return (
    <Content>
      <h1>EDITAR PACIENTE</h1>
      <label>
        Paciente:
        <input
          type="text"
          name="paciente"
          value={paciente}
          onChange={handlePaciente}
        />
      </label>

      <label>
        Familiograma:
        <input
          type="text"
          name="familigrama"
          value={familiograma}
          onChange={handleFamiliograma}
        />
      </label>

      <label>
        Antecedentes:
        <input
          type="text"
          name="antecedentes"
          value={antecedentes}
          onChange={handleAntecedentes}
        />
      </label>

      <label>
      Padecimientos:
        <input
          type="text"
          name="padecimientos"
          value={padecimientos}
          onChange={handlePadecimientos}
        />
      </label>
      <label>
      Tratamiento:
        <input
          type="text"
          name="tratamientos"
          value={tratamientos}
          onChange={handleTratamientos}
        />
      </label>
      <label>
      Diagnostico:
        <input
          type="text"
          name="diagnostico"
          value={diagnostico}
          onChange={handleDiagnostico}
        />
      </label>
      
      <button onClick={handleSubmit}>Actualizar</button>
    </Content>
  );
};
export default ExpedienteEdit;
