import React, { useState, useEffect } from "react";
import Content from "../../components/Content";
import axios from "axios";
import PacienteServices from "../../services/PacienteServices";
import Button from "@mui/material/Button";
import IconButton from "@mui/material/IconButton";
import EditIcon from "@mui/icons-material/Edit";
import DeleteIcon from "@mui/icons-material/Delete";
import PersonAddIcon from "@mui/icons-material/PersonAdd";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Paper from "@mui/material/Paper";
import Layout from "../../containers/Layout";
import { useParams } from "react-router-dom";


const Pacientes = () => {
  const [usuario, setUsuario]=useState([]);  // useState() sirve para renderizar la app 
  const [paciente, setPaciente]=useState([]);   // con su varible y su función
  const [idPaciente, setidPaciente] = useState("");  // los [] es porque recibe un arreglo del servidor de /pacientes
  const [nomPaciente, setnomPaciente] = useState("");  
  const [ap1Paciente, setap1Paciente] = useState("");
  const [ap2Paciente, setap2Paciente] = useState("");
  const [edadPaciente, setedadPaciente] = useState("");
  const [psicologo, setPsicologo] = useState([]);
  const [idPsicologo, setidPsicologo] = useState("");
  const [nomPsicologo, setnomPsicologo] = useState("");
  const [ap1Piscologo, setap1Psicologo] = useState("");
  const [ap2Psicologo, setap2Psicologo] = useState("");

  const server=`http://localhost:4002`;
  const routeParams = useParams();

  const getData = async () => {

    const  data3  = await axios.get(`${server}/usuarios`);
    console.log("1-- User --",data3.data);
    setUsuario(data3.data);
    
    const data = await axios.get(`${server}/pacientes?filter=` + JSON.stringify({
      "where": {
        "idPaciente": routeParams.id
      },
      "include": [
        {
          "relation": "pacientes_usuarios"
        },
      ]
    }));
    console.log("1-- px - user --",data.data[0]);
    setidPaciente(data.data[0].idPaciente);
    setnomPaciente(data.data[0].pacientes_usuarios.nombre);
    setap1Paciente(data.data[0].pacientes_usuarios.ap1);
    setap2Paciente(data.data[0].pacientes_usuarios.ap2);
    setedadPaciente(data.data[0].pacientes_usuarios.edad);

    console.log("2-- px - user --",data.data[0]);
    setPaciente(data.data[0]);

    const data2 = await axios.get(`${server}/psicologos?filter=` + JSON.stringify({
      "where": {
        "idPsicologo": routeParams.id
      },
      "include": [
        {
          "relation": "psicologos_usuarios"
        },
      ]
    }));

    console.log("1-- psi - user --",data2.data[0]);
    setidPsicologo(data2.data[0].idPsicologo);
    setnomPsicologo(data2.data[0].psicologos_usuarios.nombre);
    setap1Psicologo(data2.data[0].psicologos_usuarios.ap1);
    setap2Psicologo(data2.data[0].psicologos_usuarios.ap2);

    console.log("2-- psi - user --",data2.data[0]);
    setPsicologo(data2.data);
  };
  

  useEffect(()=>{
    getData();
  },[]
  );

  const DisplayData = usuario.map((info) => {
    return (
      <TableRow
        key={info.idUsuario}
        sx={{ "&:last-child td, &:last-child th": { border: 0 } }}
      >
        {/*{<TableCell component="th" scope="row">
          {info.idPaciente}
    </TableCell>}*/}
        <TableCell>{info.nombre}</TableCell>
        <TableCell>{info.ap1}</TableCell>
        <TableCell>{info.ap2}</TableCell>
        <TableCell>{info.edad}</TableCell>
        <TableCell>{nomPsicologo}</TableCell>
        <TableCell>{ap1Piscologo}</TableCell>
        <TableCell>{ap2Psicologo}</TableCell>
        <TableCell>
          <IconButton aria-label="editar">
            <a href={`pacientes-edit/${info.idUsuario}`}>
              <EditIcon color="primary" />
            </a>
          </IconButton>
        </TableCell>
        <TableCell>
          <IconButton
            aria-label="borrar"
            onClick={async () => {
              await PacienteServices.deletePaciente(info.idPaciente);
              await getData();
            }}
          >
            <DeleteIcon color="danger" />
          </IconButton>
        </TableCell>
      </TableRow>
    );
  });
  
  return (
    <Layout>
        <Content>
        <h1> PACIENTES </h1>
        <a href="/pacientes-add">
          <Button variant="contained" startIcon={<PersonAddIcon />}>
            Agregar paciente
          </Button>
        </a>
        <div>
          
        </div>
        <div>
          <TableContainer component={Paper}>
            <Table sx={{ minWidth: 650 }} aria-label="simple table">
              <TableHead>
                <TableRow>
                  {/*<TableCell>No.</TableCell>*/}
                  <TableCell><h4>Nombre</h4></TableCell>
                  <TableCell><h4>Ap1</h4></TableCell>
                  <TableCell><h4>Ap2</h4></TableCell>
                  <TableCell><h4>Edad</h4></TableCell>
                  {/*<TableCell><h4>Sexo</h4></TableCell>*/}
                  <TableCell><h4><font color="gray">Psicologo</font></h4></TableCell>
                  <TableCell><h4><font color="gray">Ap1</font></h4></TableCell>
                  <TableCell><h4><font color="gray">Ap2</font></h4></TableCell>
                  <TableCell><h4>Editar</h4></TableCell>
                  <TableCell><h4>Borrar</h4></TableCell>
                </TableRow>
              </TableHead>
              <TableBody>{DisplayData}</TableBody>
            </Table>
          </TableContainer>
        </div>
      </Content>
    </Layout>
    
  );

};

export default Pacientes;
